VERTEX.OUTPUT:
	    void main(void) {
	    	vColor = aVertexColor;

    		vec4 position = uMVMatrix * vec4(aVertexPosition, 1.0);
			gl_Position = uPMatrix * position;
    	}

FRAGMENT.PRECISION:
#ifdef GL_ES
	precision highp float;
#endif

#define NB_POINTLIGHTS
#define NB_SPOTLIGHTS
#define NB_DIRECTIONNALLIGHTS

struct pointLight{
	vec3 position;
	vec3 color;
	vec3 intensity;
};

struct spotLight{
	vec3 position;
	vec3 direction;
	vec3 color;
	float radius;
	vec3 intensity;
};

struct directionnalLight{
	vec3 direction;
	vec3 color;
	vec3 intensity;
};

FRAGMENT.UNIFORMS:
uniform pointLight pointLights[NB_POINTLIGHTS];
uniform spotLight spotLights[NB_SPOTLIGHTS];
uniform directionnalLight directionnalLights[NB_DIRECTIONNALLIGHTS];
