VERTEX.OUTPUT:
	    void main(void) {
    		vPosition = uMVMatrix * vec4(aVertexPosition, 1.0);
			gl_Position = uPMatrix * vPosition;
    	}

FRAGMENT.PRECISION:
#ifdef GL_ES
	precision highp float;
#endif

#define NB_POINTLIGHTS
#define NB_SPOTLIGHTS
#define NB_DIRECTIONNALLIGHTS

struct pointLight{
	vec3 position;
	vec3 color;
	vec3 intensity;
};

struct spotLight{
	vec3 position;
	vec3 direction;
	vec3 color;
	float radius;
	vec3 intensity;
};

struct directionnalLight{
	vec3 direction;
	vec3 color;
	vec3 intensity;
};

FRAGMENT.UNIFORMS:
uniform pointLight pointLights[NB_POINTLIGHTS];
uniform spotLight spotLights[NB_SPOTLIGHTS];
uniform directionnalLight directionnalLights[NB_DIRECTIONNALLIGHTS];

FRAGMENT.OUTPUT:
	void main(void) {
		// color
		vec3 color = vec3(1.0,0.0,0.0);
		gl_FragColor = vec4(color, 1.0);
    }
